mapper.select:
SELECT A.* , (SELECT img_data FROM t_ails_crgw_img WHERE work_date = A.work_date AND work_no = A.work_no) imageData , (SELECT shoot_dttm FROM t_ails_crgw_img WHERE work_date = A.work_date AND work_no = A.work_no) shootDttm , (SELECT tolof_nm FROM t_ails_crgw_img WHERE work_date = A.work_date AND work_no = A.work_no) tolofNm FROM t_ails_nmrecg_clsf A JOIN t_ails_crgw_img_data B ON A.work_date = B.work_date AND A.work_no = B.work_no WHERE 1 = 1
AND A.work_date = #{searchDate}
AND A.nmrecg_cd = #{nmrecgCd}
AND IFNULL(A.pros_yn, 'N') = 'N' AND IFNULL(A.del_yn, 'N') = 'N' ORDER BY A.work_date, A.work_no

mapper.update:
update t_ails_nmrecg_clsf set
    dtrm_yn = #{dtrmYn}
    , dtrm_nmrecg_cd = #{nmrecgCd}
    , dtrm_dttm = now()
WHERE work_date = #{workDate}
    AND work_no = #{workNo}

update t_ails_crgw_img_data set
del_yn = #{delYn}
WHERE work_date = #{workDate}
AND work_no = #{workNo}

update t_ails_crgw_img_data set
pros_yn = #{prosYn}
WHERE work_date = #{workDate}
AND work_no = #{workNo}
