mapper.update:
UPDATE  t_ails_label_rslt SET
        label_data_text = #{labelDataText}
        , label_json = #{labelJson}
        , dtrm_yn = #{dtrmYn}
        , dtrm_dttm = now()
WHERE 1 = 1
    AND work_date = #{workDate}
    AND work_no = #{workNo}

     AND label_type = 'A'


     AND label_type IN ('B','C','D')


     AND label_type IN ('E')


     AND label_type IN ('F')
